# get reoccuring sequences of length in text
def get_seq_count_of_text(length, text):
    count = {}
    for i in range(len(text) - length):
        word = '' 
        for j in range(i, i+length):
            word += text[j]
        if word in count:
            count[word] += 1
        else:
            count[word] = 1
    return sorted(count.items(), key=lambda x: x[::-1], reverse=True)

# count words in array words
def count_words(words):
    counted = {}
    for word in words:
        if word in counted:
            counted[word] += 1
        else:
            counted[word] = 1
    return sorted(counted.items(), key=lambda x: x[::-1], reverse=True)

# get sequences of partially decoded text
def get_sequences(partialctext):
    count = [] 
    sequel = ''
    for char in partialctext: 
        if char.islower():
            sequel += char
        else:
            if len(sequel) > 3:
                count.append(sequel)
            sequel = ''
    return count

# if you have patterns that fit in eachother and have same count use biggest
def get_patterns(text):
    patterns = {} 
    current_pattern = ''
    for ichar in range(len(text)):
        current_pattern += text[ichar]
        # print(str(ichar / len(text) * 100) + '%')
        for pattern_size in range(len(text) - 1):
            check_pattern = ''
            min = ichar
            max = len(text) if ichar + pattern_size > len(text) else ichar + pattern_size
            if max - min > 20:
                continue
            for ipattern in range(min, max): 
                check_pattern += text[ipattern]
            # print(check_pattern)
            if len(check_pattern) > 3:
                has_lowercase = False
                for char in check_pattern:
                    if char.islower():
                        has_lowercase = True
                if has_lowercase and check_pattern in current_pattern:
                    # print('adding pattern: ' + check_pattern)
                    if check_pattern in patterns:
                        patterns[check_pattern] += 1
                    else:
                        patterns[check_pattern] = 1
    return sorted(patterns.items(), key=lambda x: x[::-1], reverse=True)

def pretty_format(sorted_list):
    return ' '.join(map(lambda x: x[0] + '[' + str(x[1]) + ']', sorted_list))


