# Color patterns and lowercase characters
patterns = list(map(lambda x: x[0], patterns))
colored_plaintext = ''
ichar = 0
while ichar < len(plaintext):
    # check if char in pattern
    for pattern_length in range(3, 20):
        check_pattern = ''
        # max is length of plaintext or char position + length
        pmax = len(plaintext) if ichar + pattern_length > len(plaintext) else ichar + pattern_length
        for i in range(ichar, pmax):
            check_pattern += plaintext[i] 
        # if it is a pattern add colored characters and continue
        if check_pattern in patterns:
            for char in check_pattern:
                colored_plaintext += '\033[92m' + char + '\033[0m'
            ichar += len(check_pattern)
            continue
    if plaintext[ichar].islower():
        colored_plaintext += '\033[93m' + plaintext[ichar] + '\033[0m'
    else:
        colored_plaintext += plaintext[ichar]
    ichar += 1


print(colored_plaintext)